import React, { useState, useEffect, useRef } from "react";
import logo from "./logo.svg";
import "./App.css";
import essay from "./text/essay.txt";
import proof from "./text/essay.txt";

function App() {
  const [num, setNum] = useState(1);
  const [facts, setFacts] = useState([]);
  const [nextPage, setNextPage] = useState("");
  const cardsRef = useRef([]);
  const [essayText, setEssayText] = useState("");
  const [pressed, isPressed] = useState(false);
  const [showSources, setShowSources] = useState(false);

  useEffect(() => {
    fetch(essay)
      .then((r) => r.text())
      .then((text) => {
        setEssayText(text);
      });
  }, []);

  // Function to split the essay text into headings and paragraphs
  const renderEssayContent = () => {
    if (!essayText) return null;

    const sections = essayText.split("\n\n"); // Split text into sections based on double line breaks
    return sections.map((section, index) => {
      if (section.startsWith("#")) {
        // Heading section
        const headingLevel = section.indexOf(" ") + 1;
        const headingTag = `h${headingLevel}`;
        const headingText = section.slice(headingLevel).trim();
        return React.createElement(headingTag, { key: index }, headingText);
      } else {
        // Paragraph section
        return <p key={index}>{section}</p>;
      }
    });
  };

  useEffect(() => {
    async function fetchData() {
      const url = `https://chicken-facts.p.rapidapi.com/facts/order/id/asc/page/${num}.json`;
      const options = {
        method: "GET",
        headers: {
          "X-RapidAPI-Key":
            "1fece9cd46msh18f98aab476cdbfp1ac901jsnd793ec542456",
          "X-RapidAPI-Host": "chicken-facts.p.rapidapi.com",
        },
      };

      try {
        const response = await fetch(url, options);
        const data = await response.json();
        setFacts((prevFacts) => [...prevFacts, ...data.facts]);
        setNextPage(data.next);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, [num]);

  const loadNextPage = () => {
    setNum(num + 1);
    if (num >= 5) {
      setNum(num - 4);
    }
  };

  const handleScroll = () => {
    if (cardsRef.current[0]) {
      const { scrollLeft, scrollWidth, clientWidth } =
        cardsRef.current[0].parentElement;

      // Check if the scroll position is at the end
      if (scrollLeft + clientWidth >= scrollWidth - 5) {
        loadNextPage();
      }
    }
  };

  useEffect(() => {
    const observerOptions = {
      root: null,
      rootMargin: "0px",
      threshold: 0.68,
    };

    const observerCallback = (entries, observer) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add("visible");
        } else {
          entry.target.classList.remove("visible");
        }
      });
    };

    const observer = new IntersectionObserver(
      observerCallback,
      observerOptions
    );

    cardsRef.current.forEach((card) => {
      if (card) {
        observer.observe(card);
      }
    });

    return () => {
      cardsRef.current.forEach((card) => {
        if (card) {
          observer.unobserve(card);
        }
      });
    };
  }, [facts]);

  return (
    <div className="App">
      {pressed ? (
        <div className="mainInfo" onClick={() => isPressed(!pressed)}>
          {renderEssayContent()}
        </div>
      ) : (
        <button className="mainInfoEmpty" onClick={() => isPressed(!pressed)}>
          Full Text
        </button>
      )}
      <button
        className="mainInfoEmpty"
        onClick={() => setShowSources(!showSources)}
      >
        Sources
      </button>
      <div className="sources"></div>
      <div className="cardsDisplay">
        <i className="fa-solid fa-arrow-right-long"></i>
        <div className="cards" onScroll={handleScroll}>
          {facts.map((fact, index) => (
            <div
              key={`${fact.id}-${index}`}
              className="card"
              ref={(el) => (cardsRef.current[index] = el)}
            >
              <div className="topCard">
                <i className="fa-solid fa-egg"></i>
                <h3>{fact.fact}</h3>
              </div>
              <div className="info">
                <h5>
                  Source:{" "}
                  <a
                    href={fact.source}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {fact.source}
                  </a>
                </h5>
                <h6>Published: {fact.published}</h6>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
