import React, { useState, useEffect, useRef } from "react";
import logo from "./logo.svg";
import "./App.css";
import essay from "./text/essay.txt";

function App() {
  const [num, setNum] = useState(1);
  const [facts, setFacts] = useState([]);
  const [nextPage, setNextPage] = useState("");
  const cardsRef = useRef([]);
  const [essayText, setEssayText] = useState("");
  const [pressed, isPressed] = useState(false);
  const [info, isInfo] = useState(false);

  fetch(essay)
    .then((r) => r.text())
    .then((text) => {
      setEssayText(text);
    });

  useEffect(() => {
    async function fetchData() {
      const url = `https://chicken-facts.p.rapidapi.com/facts/order/id/asc/page/${num}.json`;
      const options = {
        method: "GET",
        headers: {
          "X-RapidAPI-Key":
            "1fece9cd46msh18f98aab476cdbfp1ac901jsnd793ec542456",
          "X-RapidAPI-Host": "chicken-facts.p.rapidapi.com",
        },
      };

      try {
        const response = await fetch(url, options);
        const data = await response.json();
        setFacts((prevFacts) => [...prevFacts, ...data.facts]);
        setNextPage(data.next);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, [num]);

  const loadNextPage = () => {
    setNum(num + 1);
    if (num >= 5) {
      setNum(num - 4);
    }
  };

  const handleScroll = () => {
    if (cardsRef.current[0]) {
      const { scrollLeft, scrollWidth, clientWidth } =
        cardsRef.current[0].parentElement;

      // Check if the scroll position is at the end
      if (scrollLeft + clientWidth >= scrollWidth - 5) {
        loadNextPage();
      }
    }
  };

  useEffect(() => {
    const observerOptions = {
      root: null,
      rootMargin: "0px",
      threshold: 0.68,
    };

    const observerCallback = (entries, observer) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add("visible");
        } else {
          entry.target.classList.remove("visible");
        }
      });
    };

    const observer = new IntersectionObserver(
      observerCallback,
      observerOptions
    );

    cardsRef.current.forEach((card) => {
      if (card) {
        observer.observe(card);
      }
    });

    return () => {
      cardsRef.current.forEach((card) => {
        if (card) {
          observer.unobserve(card);
        }
      });
    };
  }, [facts]);

  return (
    <div className="App">
      <div className="buttons">
        {pressed ? (
          <button className="mainInfo" onClick={() => isPressed(!pressed)}>
            {essayText}
          </button>
        ) : (
          <button className="mainInfoEmpty" onClick={() => isPressed(!pressed)}>
            Full Text
          </button>
        )}
        {info ? (
          <button className="mainInfo" onClick={() => isInfo(!info)}>
References
Bauman, J. A. (2016, September 27). Kauai’s wild chickens: The good, the bad, and the ugly! Kauai Blog. Retrieved February 2, 2024, from https://kauaiblog.wordpress.com/2013/03/12/kauais-wild-chickens-the-good-the-bad-an the-ugly/
Division of Extension. (2024) Origin and history of the chicken. Livestock. University of Wisconsin-Madison. Retrieved March 5, 2024, from https://livestock.extension.wisc.edu/articles/origin-and-history-of-the-chicken/ 
Feather Lover Farms. (2023, January 18). The Hawaiian Chicken History and origin. Retrieved February 2, 2024, from https://featherloverfarms.com/blogs/feather-lover-farms-blog/the-hawaiian-chicken-history-and-origin#:~:text=Over%20time%2C%20the%20wild%20Hawaiian,were%20hunted%20for%20their%20meat 
Li, D., Sun, G., Zhang, M., Cao, Y., Zhang, C., Fu, Y., Li, F., Li, G., Jiang, R., Han, R., Li, Z., Wang, Y., Tian, Y., Liu, X., Li, W., & Kang, X. (2020). Breeding history and candidate genes responsible for black skin of Xichuan black-bone chicken. BMC Genomics, 21(1), 1–15. https://doi.org/10.1186/s12864-020-06900-8 
McCall, D. (1978). The Marvelous Chicken and Its Companion in Yoruba Art and Myth. JSTOR. https://www.jstor.org/stable/40341607?seq=6 

National Park Service. (2022, March 29). Queen Lili'uokalani. National Park Service. Retrieved February 20, 2024, from https://www.nps.gov/articles/000/queen-lili-uokalani.htm 
Schlosser, S.E. (2022, August 28). Blue Hen’s chicks. American Folklore. Retrieved February 12, 2024, from https://americanfolklore.net/folklore/2010/08/blue_hens_chicks.html 
Seligmann, L. J. (1987). The Chicken in Andean History and Myth: The Quechua Concept of Wallpa. Ethnohistory, 34(2), 139. https://doi.org/10.2307/482251 
Tikkanen, Amy. (2009, June 8). Kuna. Encyclopedia Britannica. https://www.britannica.com/topic/Kuna  
U.S. National Archives and Records Administration. (2022, February 8). Joint Resolution to Provide for Annexing the Hawaiian Islands to the United States (1898). NATIONAL ARCHIVES. Retrieved February 2, 2024, from https://www.archives.gov/milestone-documents/joint-resolution-for-annexing-the-hawaiian-islands#:~:text=On%20July%207%2C%201898%2C%20the,control%20of%20the%20Hawaiian%20government
Wikimedia Foundation. (2024, February 12). Cultural references to chickens. Wikipedia. Retrieved February 12, 2024, from https://en.wikipedia.org/wiki/Cultural_references_to_chickens#:~:text=Kaharingan%2C%20an%20animist%20folk%20religion,introduced%20to%20them%20by%20god. 

          </button>
        ) : (
          <button className="mainInfoEmpty" onClick={() => isInfo(!info)}>
            Sources
          </button>
        )}
      </div>
      <div className="cardsDisplay">
        <i className="fa-solid fa-arrow-right-long"></i>
        <div className="cards" onScroll={handleScroll}>
          {facts.map((fact, index) => (
            <div
              key={`${fact.id}-${index}`}
              className="card"
              ref={(el) => (cardsRef.current[index] = el)}
            >
              <div className="topCard">
                <i className="fa-solid fa-egg"></i>
                <h3>{fact.fact}</h3>
              </div>
              <div className="info">
                <h5>
                  Source:{" "}
                  <a
                    href={fact.source}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {fact.source}
                  </a>
                </h5>
                <h6>Published: {fact.published}</h6>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
