import logo from "./logo.svg";
import "./App.css";
import React, { useState, useEffect, useRef } from "react";

function App() {
  const [num, setNum] = useState(1);
  const [facts, setFacts] = useState([]);
  const [nextPage, setNextPage] = useState("");
  const cardsRef = useRef(null);

  useEffect(() => {
    async function fetchData() {
      const url = `https://chicken-facts.p.rapidapi.com/facts/order/id/asc/page/${num}.json`;
      const options = {
        method: "GET",
        headers: {
          "X-RapidAPI-Key":
            "1fece9cd46msh18f98aab476cdbfp1ac901jsnd793ec542456",
          "X-RapidAPI-Host": "chicken-facts.p.rapidapi.com",
        },
      };

      try {
        const response = await fetch(url, options);
        const data = await response.json();
        setFacts(data.facts);
        setNextPage(data.next);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, [num]);

  const loadNextPage = () => {
    setNum(num + 1);
    if (num >= 5) {
      setNum(num - 4);
    }
  };
  const loadLastPage = () => {
    setNum(num - 1);
    if (num <= 1) {
      setNum(num + 4);
    }
  };
  const handleScroll = () => {
    if (cardsRef.current) {
      const { scrollLeft, scrollWidth, clientWidth } = cardsRef.current;
      if (scrollLeft + clientWidth >= scrollWidth - 5) {
        loadNextPage();
      }
    }
  };

  return (
    <div className="App">
      <div className="cardsDisplay">
      <p>{num} out of 5</p>
      <div className="buttons">
          <button onClick={loadLastPage}>Last Page</button>
          <button onClick={loadNextPage}>Next Page</button>
        </div>
        <div className="cards" onScroll={handleScroll} ref={cardsRef}>
        {facts.map((fact) => (
          <div key={fact.id} className="card">
            <h3>{fact.fact}</h3>
            <h5>
              Source:{" "}
              <a href={fact.source} target="_blank" rel="noopener noreferrer">
                {fact.source}
              </a>
            </h5>
            <h6>Published: {fact.published}</h6>
          </div>
        ))}
        </div>
      </div>
    </div>
  );
}

export default App;
