import React, { useState, useEffect, useRef } from "react";
import logo from "./logo.svg";
import "./App.css";

function App() {
  const [num, setNum] = useState(1);
  const [facts, setFacts] = useState([]);
  const [nextPage, setNextPage] = useState("");
  const cardsRef = useRef([]);

  useEffect(() => {
    async function fetchData() {
      const url = `https://chicken-facts.p.rapidapi.com/facts/order/id/asc/page/${num}.json`;
      const options = {
        method: "GET",
        headers: {
          "X-RapidAPI-Key": "1fece9cd46msh18f98aab476cdbfp1ac901jsnd793ec542456",
          "X-RapidAPI-Host": "chicken-facts.p.rapidapi.com",
        },
      };

      try {
        const response = await fetch(url, options);
        const data = await response.json();
        setFacts((prevFacts) => [...prevFacts, ...data.facts]);
        setNextPage(data.next);
      } catch (error) {
        console.error(error);
      }
    }

    fetchData();
  }, [num]);

  const loadNextPage = () => {
    setNum((prevNum) => prevNum + 1);
  };

  const loadLastPage = () => {
    setNum((prevNum) => Math.max(1, prevNum - 1));
  };

  const handleScroll = () => {
    if (cardsRef.current[0]) {
      const { scrollLeft, scrollWidth, clientWidth } = cardsRef.current[0].parentElement;

      // Check if the scroll position is at the end
      if (scrollLeft + clientWidth >= scrollWidth - 5) {
        loadNextPage();
      }

      // Check if the scroll position is at the beginning
      if (scrollLeft <= 5) {
        loadLastPage();
      }
    }
  };

  useEffect(() => {
    const observerOptions = {
      root: null,
      rootMargin: "0px",
      threshold: 0.1
    };

    const observerCallback = (entries, observer) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');
        } else {
          entry.target.classList.remove('visible');
        }
      });
    };

    const observer = new IntersectionObserver(observerCallback, observerOptions);

    cardsRef.current.forEach(card => {
      if (card) {
        observer.observe(card);
      }
    });

    return () => {
      cardsRef.current.forEach(card => {
        if (card) {
          observer.unobserve(card);
        }
      });
    };
  }, [facts]);

  return (
    <div className="App">
      <div className="cardsDisplay">
        <p>{num} out of 5</p>
        <div className="buttons">
          <button onClick={loadLastPage}>Last Page</button>
          <button onClick={loadNextPage}>Next Page</button>
        </div>
        <div className="cards" onScroll={handleScroll}>
          {facts.map((fact, index) => (
            <div
              key={fact.id}
              className="card"
              ref={(el) => (cardsRef.current[index] = el)}
            >
              <h3>{fact.fact}</h3>
              <h5>
                Source:{" "}
                <a href={fact.source} target="_blank" rel="noopener noreferrer">
                  {fact.source}
                </a>
              </h5>
              <h6>Published: {fact.published}</h6>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
